function inlineSVG(){$("img.svg").not(".replaced-svg").each(function(){var theIMG=jQuery(this),imgID=theIMG.attr("id"),imgClass=theIMG.attr("class"),imgURL=theIMG.attr("src"),imgAlt=theIMG.attr("alt");$.get(imgURL,function(data){var theSVG=$(data).find("svg");if(typeof imgID!=="undefined"){theSVG=theSVG.attr("id",imgID)}if(typeof imgClass!=="undefined"){theSVG=theSVG.attr("class",imgClass+" replaced-svg")}theSVG=theSVG.removeAttr("xmlns:a width height x y enable-background xmlns:xlink xml:space").attr("aria-label",imgAlt).prepend("<title>"+imgAlt+"</title>");theIMG.replaceWith(theSVG)})})}